void main()
{
	int arr[3][3],flag=0;
	printf("Enter elements of array : \n\n");
	for(int i=0;i<3;i++)
	{
		for(int j=0;j<3;j++)
		{
			scanf("%d",&arr[i][j]);
		}
	}

	for(int i=0;i<3;i++)
	{
		for(int j=0;j<3;j++)
		{
			if(arr[i][j]==arr[j][i])
			{
				continue;
			}
			else
			{
				flag=1;
			}
		}
	}
	if(flag==0)
	{
		printf("The matrix is symmetric.\n");
	}
	else
	{
		printf("The matrix is not symmetric.\n");
	}
}

